mapper.select:
SELECT
    id, platform_name platformName, app_id appId, app_secret appSecret,
    contact_name contactName, mobile, mark, expiry_date expiryDate,
    status, created_user createdUser, created_date createdDate,
    modified_user modifiedUser, modified_date modifiedDate
FROM
    third_platform


        app_id = #{appId}


        platform_name = #{platformName}


        AND contact_name = #{contactName}


        AND mobile = #{mobile}


         AND expiry_date <= #{expiryDate}


        AND status = #{status}


ORDER BY id

mapper.insert:
INSERT INTO third_platform
platform_name, app_id, app_secret, contact_name, mobile, mark, expiry_date, created_user
#{platformName}, #{appId}, #{appSecret}, #{contactName}, #{mobile}, #{mark}, #{expiryDate}, #{createdUser}
